var fs = require('fs');

var heap = require('./lib/btree');

var a = [6, 5, 3, 1, 8, 7, 2, 4];
var count = a.length;

var h = new heap();

while(a.length) {
  h.insert({}, a.pop());
}

console.log('digraph {');
h.print();
console.log('}');

while (h.length) {
  console.log('popping', h.pop()._key);
  h.print();
}
